import React, { useState, useEffect } from 'react'
import { Statistic, Icon, Container, Segment, Dimmer, Loader } from 'semantic-ui-react'
import { LoginApi } from '../misc/LoginApi'
import { handleLogError } from '../misc/Helpers'

function Home() {
  const [numberOfUsers, setNumberOfUsers] = useState(0)
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    const fetchData = async () => {
      setIsLoading(true)
      try {
        const responseUsers = await LoginApi.numberOfUsers()
        setNumberOfUsers(responseUsers.data)
      } catch (error) {
        handleLogError(error)
      } finally {
        setIsLoading(false)
      }
    }

    fetchData()
  }, [])

  if (isLoading) {
    return (
      <Segment basic style={{ marginTop: window.innerHeight / 2 }}>
        <Dimmer active inverted>
          <Loader inverted size='huge'>Loading</Loader>
        </Dimmer>
      </Segment>
    )
  }

  return (
        <Container text style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', height: '100vh' }}>
          <Segment color='blue'>
            <Statistic text style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', minHeight : '10vh' }}>
              <Statistic.Label>Registered Users</Statistic.Label>
              <Statistic.Value><Icon name='user' color='grey' />{numberOfUsers}</Statistic.Value>
            </Statistic>
          </Segment>
          <Statistic.Label text style={{ display: 'flex', flexDirection: 'column', textAlign: 'center', fontSize : '10vh' }}>Hola Mundo</Statistic.Label>
        </Container>
  )
}

export default Home
