import React, { useState } from 'react'
import { NavLink, Navigate } from 'react-router-dom'
import {Button, Form, Grid, Segment, Message, Statistic} from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { LoginApi } from '../misc/LoginApi'
import { handleLogError } from '../misc/Helpers'

function RecoveryPassword() {
  const Auth = useAuth()
  const isLoggedIn = Auth.userIsAuthenticated()

  const [email, setEmail] = useState('')
  const [name, setName] = useState('')
  const [cpf, setCpf] = useState('')
  const [newPassword, setNewPassword] = useState('')
  const [isError, setIsError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  const handleInputChange = (e, { name, value }) => {
    if (name === 'email') {
      setEmail(value)
    } else if (name === 'name') {
      setName(value)
    } else if (name === 'cpf') {
      setCpf(value)
    } else if (name === 'newPassword') {
      setNewPassword(value)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!(email && name && cpf && newPassword)) {
      setIsError(true)
      setErrorMessage('Please, inform all fields!')
      return
    }

    const user = { email, name, cpf, newPassword }


    try {
      const response = await LoginApi.signup(user)
      const { id, name, role } = response.data
      const authdata = window.btoa(email + ':' + newPassword)
      const authenticatedUser = { id, name, role, authdata }

      Auth.userLogin(authenticatedUser)

      setEmail('')
      setName('')
      setCpf('')
      setNewPassword('')
      setIsError(false)
      setErrorMessage('')
    } catch (error) {
      handleLogError(error)
      if (error.response && error.response.data) {
        const errorData = error.response.data
        let errorMessage = 'Invalid fields'
        if (errorData.status === 409) {
          errorMessage = errorData.message
        } else if (errorData.status === 400) {
          errorMessage = errorData.errors[0].defaultMessage
        }
        setIsError(true)
        setErrorMessage(errorMessage)
      }
    }
  }

  if (isLoggedIn) {
    return <Navigate to='/' />
  }

  return (
      <Grid textAlign='center'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Form size='large' onSubmit={handleSubmit}>
            <Statistic.Label>Recovery Password</Statistic.Label>
            <Segment>
              <Form.Input
                  fluid
                  autoFocus
                  name='email'
                  icon='user'
                  iconPosition='left'
                  placeholder='Email'
                  value={email}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='name'
                  icon='address card'
                  iconPosition='left'
                  placeholder='Name'
                  value={name}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='cpf'
                  icon='id card'
                  iconPosition='left'
                  placeholder='CPF'
                  value={cpf}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='newPassword'
                  icon='lock'
                  iconPosition='left'
                  placeholder='New Password'
                  type='password'
                  value={newPassword}
                  onChange={handleInputChange}
              />
              <Button color='blue' fluid size='large'>Recover Password</Button>
            </Segment>
          </Form>
          <Message>{`Remembered your password? `}
            <NavLink to="/login" color='teal'>Login</NavLink>
          </Message>
          {isError && <Message negative>{errorMessage}</Message>}
        </Grid.Column>
      </Grid>
  )
}

export default RecoveryPassword
