import React, { useEffect, useState } from 'react'
import { Navigate } from 'react-router-dom'
import { Container } from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { LoginApi } from '../misc/LoginApi'
import AdminTab from './AdminTab'
import { handleLogError } from '../misc/Helpers'

function AdminPage() {
  const Auth = useAuth()
  const user = Auth.getUser()
  const isAdmin = user.role === 'ADMIN'

  const [users, setUsers] = useState([])
  const [userEmailSearch, setUserEmailSearch] = useState('')
  const [isUsersLoading, setIsUsersLoading] = useState(false)


  useEffect(() => {
    handleGetUsers()
  }, [])

  const handleInputChange = (e, { name, value }) => {
    if (name === 'userEmailSearch') {
      setUserEmailSearch(value)
    }
  }

  const handleGetUsers = async () => {
    try {
      setIsUsersLoading(true)
      const response = await LoginApi.getUsers(user)
      const users = response.data
      setUsers(users)
    } catch (error) {
      handleLogError(error)
    } finally {
      setIsUsersLoading(false)
    }
  }

  const handleDeleteUser = async (email) => {
    try {
      await LoginApi.deleteUser(user, email)
      await handleGetUsers()
    } catch (error) {
      handleLogError(error)
    }
  }

  const handleSearchUser = async () => {
    try {
      const response = await LoginApi.getUsers(user, userEmailSearch)
      const data = response.data
      const users = data instanceof Array ? data : [data]
      setUsers(users)
    } catch (error) {
      handleLogError(error)
      setUsers([])
    }
  }



  if (!isAdmin) {
    return <Navigate to='/' />
  }
const handleChangeRole = async (email, newRole) => {
  try {
    await LoginApi.changeUserRole(user, email, newRole);
    await handleGetUsers();
  } catch (error) {
    handleLogError(error);
  }
};
  return (
      <Container>
        <AdminTab
            isUsersLoading={isUsersLoading}
            users={users}
            userEmailSearch={userEmailSearch}
            handleDeleteUser={handleDeleteUser}
            handleSearchUser={handleSearchUser}
            handleInputChange={handleInputChange}
            handleChangeRole={handleChangeRole}

        />
      </Container>
  )
}

export default AdminPage
