import React, { useState } from 'react'
import { NavLink, Navigate } from 'react-router-dom'
import {Button, Form, Grid, Segment, Message, Statistic} from 'semantic-ui-react'
import { useAuth } from '../context/AuthContext'
import { LoginApi } from '../misc/LoginApi'
import { handleLogError } from '../misc/Helpers'

function validateEmail(email) {
  const re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
  return re.test(String(email).toLowerCase());
}

function validatePassword(password) {
  const re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[a-zA-Z\d]{8,}$/;
  return re.test(password);
}

function Signup() {
  const Auth = useAuth()
  const isLoggedIn = Auth.userIsAuthenticated()

  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [name, setName] = useState('')
  const [cpf, setCpf] = useState('')
  const [isError, setIsError] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')

  const handleInputChange = (e, { name, value }) => {
    if (name === 'email') {
      setEmail(value.toLowerCase())
    } else if (name === 'password') {
      setPassword(value)
    } else if (name === 'name') {
      setName(value.replace(/\b\w/g, l => l.toUpperCase()))
    } else if (name === 'cpf') {
      setCpf(value)
    }
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!validateEmail(email)) {
      setIsError(true)
      setErrorMessage('Please, enter a valid email!')
      return
    }

    if (!validatePassword(password)) {
      setIsError(true)
      setErrorMessage('Password must have at least 8 characters, including one uppercase letter, one lowercase letter, and one number.')
      return
    }

    if (!(email && password && name && cpf)) {
      setIsError(true)
      setErrorMessage('Please, inform all fields!')
      return
    }

    const user = { email, password, name, cpf }

    try {
      const response = await LoginApi.signup(user)
      const { id, name, role } = response.data
      const authdata = window.btoa(email + ':' + password)
      const authenticatedUser = { id, name, role, authdata }

      Auth.userLogin(authenticatedUser)

      setEmail('')
      setPassword('')
      setName('')
      setCpf('')
      setIsError(false)
      setErrorMessage('')
    } catch (error) {
      handleLogError(error)
      if (error.response && error.response.data) {
        const errorData = error.response.data
        let errorMessage = 'Invalid fields'
        if (errorData.status === 409) {
          errorMessage = errorData.message
        } else if (errorData.status === 500) {
          errorMessage = errorData.error.response.data
        }else if (errorData.status === 400) {
          errorMessage = errorData.errors[0].defaultMessage
        }
        setIsError(true)
        setErrorMessage(errorMessage)
      }
    }
  }

  if (isLoggedIn) {
    return <Navigate to='/' />
  }

  return (
      <Grid textAlign='center'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Form size='large' onSubmit={handleSubmit}>
            <Statistic.Label>Sign up</Statistic.Label>
            <Segment>
              <Form.Input
                  fluid
                  autoFocus
                  name='email'
                  icon='user'
                  iconPosition='left'
                  placeholder='Email'
                  value={email}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='password'
                  icon='lock'
                  iconPosition='left'
                  placeholder='Password'
                  type='password'
                  value={password}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='name'
                  icon='address card'
                  iconPosition='left'
                  placeholder='Name'
                  value={name}
                  onChange={handleInputChange}
              />
              <Form.Input
                  fluid
                  name='cpf'
                  icon='id card'
                  iconPosition='left'
                  placeholder='CPF (WITHOUT DOTS AND HYPHEN)'
                  value={cpf}
                  onChange={handleInputChange}
              />
              <Button color='blue' fluid size='large'>Signup</Button>
            </Segment>
          </Form>
          <Message>{`Already have an account? `}
            <NavLink to="/login" color='teal'>Login</NavLink>
          </Message>
          <Message>{`Forgot your password? `}
            <NavLink to="/reset-password" color='teal'>Recover</NavLink>
          </Message>
          {isError && <Message negative>{errorMessage}</Message>}
        </Grid.Column>
      </Grid>
  )
}
